// export default App;

//must use usestate to state variables that we can hook from
import React, {useState} from "react";
import "./App.css";

function City({city, state, lat, long, estimatedPopulation, totalWages}) 
{
  //how the zip code information will be displayed
  return (
    <div class="row city">
  <div class="box">
  <div class="title">{city},<b> </b>{state}</div>
    <div class="sub">
      <ul>
        <li><b>State: </b>{state}</li>
        <li><b>Location: </b> ({lat},<b> </b>{long})</li>
        <li><b>Population (estimated): </b> {estimatedPopulation} </li>
        <li><b>Estimated Wages: </b> {totalWages} </li>
      </ul>
    </div>
    </div>
  </div>
  );
}

//Handles zip input
function ZipInput({handleChange, value}) 
{
  return (
  <div class="inputBox">
    <input class="input" name="zip" placeholder="Try 11006" type="text" onChange={handleChange} value={value}>
    </input>
  </div>
  );
}

function App() 
{
  //Zip code
  const [zip, setZip] = useState("");
  //holds zip info from api event handler in array
  const [ZipInfo,setZipInfo]= useState([]);
  //event handler that  lets zipInfo hold array of information
  const zipEvent = (event) =>{
    const tempZip = event.target.value;
    //if the zip length is 5, it will fetch from the api
    if (zip && tempZip.length === 6) {
      fetch(`https://ctp-zip-api.herokuapp.com/zip/${tempZip}`)
        .then((res) => res.json())
        .then((City) => setZipInfo(City))
        .catch(() => setZipInfo([]));
      // when zip is valid it catches info
    }
    else {
      setZip(tempZip)};
  };
  return (
    <div className="App">
      <div className="App-header">
        <h1>Zip Code Search</h1>
      </div>
      <div className="container" class="row text-center">
        <div class="row">
        <b>Zip Code: </b>
        <div class="col align-center">
        <ZipInput value={zip} handleChange={zipEvent}/>
        </div>
        </div>
        <div class="row align-center">
          {//maps the zipInfo array into the new zipData matching each CITY data paremeter to the api's paremeter
          ZipInfo.map((zipData) => (
            <City
              state={zipData.State}
              city={zipData.City}
              lat={zipData.Lat}
              long={zipData.Long}
              estimatedPopulation={zipData.EstimatedPopulation}
              totalWages={zipData.TotalWages}
            />
          ))
          }
          {//if the zipInfo is 0, it will display No results
          ZipInfo.length===0 && <b> No results</b>
          }
        </div>
      </div>
    </div>
  );
}


export default App;
